  [zero] at {$variable} equals {$x}, slope is {$absoluteFirstDerivativeValue}, dot at {$firstDerivativeValue}
  [positive] at {$variable} equals {$x}, slope is positive {$absoluteFirstDerivativeValue}, dot at {$firstDerivativeValue}
  *[negative] at {$variable} equals {$x}, slope is negative {$absoluteFirstDerivativeValue}, dot at {$firstDerivativeValue}
}

{ $graphSet ->
  [primaryFirstDerivative] at {$variable} equals {$x}, f of {$variable} is {$y}, f prime of {$variable} is {$firstDerivativeValue}
  [integralPrimary] at {$variable} equals {$x}, integral is {$integralValue}, f of {$variable} is {$y}
  [integralPrimaryFirstDerivative] at {$variable} equals {$x}, integral is {$integralValue}, f of {$variable} is {$y}, f prime of {$variable} is {$firstDerivativeValue}
  *[primaryFirstDerivativeSecondDerivative] at {$variable} equals {$x}, f of {$variable} is {$y}, f prime of {$variable} is {$firstDerivativeValue}, f double prime of {$variable} is {$secondDerivativeValue}
}

{ $sign ->
